                 data-category="{{ item.category }}" 
                 data-stock="{{ 'low' if item.is_low_stock else 'good' }}">
                <div class="card inventory-card h-100" onclick="window.location.href='{{ url_for('main.inventory_detail', item_id=item._id) }}'">
