                                <p class="text-muted mb-2">
                                    <i class="bi bi-shop"></i> <strong>Supplier:</strong> {{ item.supplier }}
                                </p>
                            {% endif %}

                            {% if item.expiration_date %}
                                <p class="text-muted mb-2">
                                    <i class="bi bi-calendar-x"></i> <strong>Expires:</strong> {{ item.expiration_date.strftime('%Y-%m-%d') }}
